mapper.update:
UPDATE COMPANY SET
NAME = #{name}, COMPANY_TYPE = #{companyType},
WHERE ID = #{id}

mapper.select:
select A.ID, A.NAME,
A.COMPANY_TYPE, B.NAME AS COMPANY_TYPE_NAME,
A.CREATED_AT
from COMPANY A
JOIN COMPANY_TYPE B ON A.COMPANY_TYPE = B.ID

    ORDER BY ${sortField} ${sortOrder}


    OFFSET #{offset}


    LIMIT #{limit}
